import * as React from 'react';
import {StatusBar} from 'expo-status-bar'
import { StyleSheet, Text, View, TextInput, Button, TouchableOpacity } from 'react-native';

export default function Login() {
  const [signup, setSignup] = React.useState(false);
  const [email, setEmail] = React.useState('');
  const [password, setPassword] = React.useState('');

  return (
    <View style={styles.container}>
        <Text style={styles.title}>STRIVE</Text>
        <View style={styles.inputView}>
          <TextInput placeholder="Email" onChangeText={e => setEmail(e)} style={styles.inputContent}></TextInput>
        </View>
        <View style={styles.inputView}>
          <TextInput placeholder="Password" onChangeText={e => setEmail(e)} style={styles.inputContent}></TextInput>    
        </View>
        <TouchableOpacity style={styles.loginBtn}>
          <Text style={styles.loginText}>Log In</Text>
        </TouchableOpacity>
        <View style={styles.newUserView}>
          <Text>Not a user? 
            <TouchableOpacity><Text style={styles.newUser}> Create an account!</Text></TouchableOpacity>
          </Text>
        </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 56,
    marginBottom: 24,
  },
  inputView: {
    backgroundColor: "whitesmoke",
    borderRadius: 4,
    width: "80%",
    height: 44,
    marginBottom: 16,
    borderColor: 'lightgray',
    borderWidth: 1
  },
  inputContent: {
    padding: 12,
    flex: 1
  },
  /*forgot_button: {
    height: 30,
    marginBottom: 30,
  },*/
  loginBtn: {
    width: "80%",
    borderRadius: 8,
    height: 50,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 40,
    backgroundColor: "#FAD0C9FF",
  },
  loginText: {
    color: 'white'
  },
  newUserView: {
    position: 'absolute',
    bottom: 20,
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    height: 80,
    width: '100%',
    borderTopColor: 'lightgray',
    borderTopWidth: 1
  },
  newUser: {
    color: 'blue',
    padding: 0,
    margin: 0,
    lineHeight: 0,
  }
});
